import React from 'react';
import CurrentExpirience from './CurrentExpirience';

import AboutUs from './AboutUs';
import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';

class Experience extends React.Component {
    state = { 
        experience: null
     }

    render() { 
        return ( 
               <Container>
            <Row>
                <Col xs={12} md={8}>
                <div className="profileWrapper">

                    <div className="botProfileWrapper">
                    <h3 style={h3Style}>Experience</h3> {this.state.expirience
                .map((u,i) => ( <CurrentExpirience usExpData={u} key={i} />))}
                    </div>
                </div> 
                </Col>

                <Col xs={6} md={4}>
                <div className="rightSidebar">

                </div>
                </Col>
            </Row>
        </Container>


        );
    }



    componentDidMount = async ()=> {
        let resp = await fetch("https://strive-school-testing-apis.herokuapp.com/api/profile/user23/experiences", {
            method: "GET",
            headers: {
                "Authorization": "basic dXNlcjIzOjJhazlFNXFxQkt2VjJ3a3k="

            }
        })

        let exp = await resp.json()
        this.setState({
            experience: exp
        })
    }
}

export default Experience;
