import React from 'react';
import CurrentExpirience from './CurrentExpirience';

import AboutUs from './AboutUs';
import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';

class Experience extends React.Component {
    state = { 
        experience: null
     }

    render() { 
        return ( 
               <Container>
            <Row>
                <Col xs={12} md={8}>
                <div className="profileWrapper">

                    <div className="botProfileWrapper">
                    <h3 >Experience</h3> {this.state.expirience.map((u,i) => ( <CurrentExpirience usExpData={u} key={i} />))}
                    </div>
                </div> 
                </Col>

                <Col xs={6} md={4}>
                <div className="rightSidebar">

                </div>
                </Col>
            </Row>
        </Container>


        );
    }

    fetchingExpirience = async() => {
        this.setState({
            loading: true
        })
        let username = "user16"
        let password = "c9WEUxMS294hN6fF"
        let token = btoa(username + ":" + password)
        let response = await fetch("https://strive-school-testing-apis.herokuapp.com/api/profile/" + this.props.match.params.userId + "/experiences", {
            method: "GET",
            headers: {
                "Authorization": "Basic " + token

            }
        })
        let prof = await response.json()

        setTimeout(() => {
            this.setState({
                expirience: prof,
                loading: false
            })
        }, 1500)
    }

    componentDidMount = () => {

        this.fetchingExpirience()

    }


}



export default Experience;
